package com.example.navicode.locationapi.entity;

import jakarta.persistence.*;
import lombok.Getter;
import lombok.Setter;

@Entity
@Getter
@Setter
public class Location {

    @Id
    private String navicode;

    private double lat;
    private double lon;

    private String name;
    private String description;

    private String type; // Static or Dynamic

    private String username;
}

# 공유용 환경 설정 (환경변수 사용)
spring.datasource.url=${DB_URL:jdbc:h2:mem:testdb}
spring.datasource.username=${DB_USERNAME:sa}
spring.datasource.password=${DB_PASSWORD:password}
spring.datasource.driver-class-name=${DB_DRIVER:org.h2.Driver}

# H2 콘솔 (H2 사용시에만)
spring.h2.console.enabled=${H2_CONSOLE_ENABLED:true}

# JPA 설정
spring.jpa.hibernate.ddl-auto=${JPA_DDL_AUTO:create-drop}
spring.jpa.database-platform=${JPA_DIALECT:org.hibernate.dialect.H2Dialect}

# JWT 설정
jwt.secret=${JWT_SECRET:yourVeryVerySecretKeyThatShouldBeLongEnoughForHS256Algorithm}
jwt.expiration=${JWT_EXPIRATION:3600000}
